var check_customer = function(frm){
  $.each(frm.doc.links || [], function(i, v) {
    console.log("test1");
    frappe.call({
         method: 'frappe.client.get',
         args: {
             'doctype': 'Customer',
             'filters': {'name': v.link_name}
         },
         callback: function(r){
            if(r.message.customer_type == "Business"){
              frm.set_df_property('tax_category', 'reqd', 1);
              frm.set_df_property('gstin', 'reqd', 1);
              frm.set_df_property('gst_state', 'reqd', 1);
              frm.set_df_property('gst_state_number', 'reqd', 1);
              frm.refresh_field('tax_category');
              frm.refresh_field('gstin');
              frm.refresh_field('gst_state_number');
              frm.refresh_field('gst_state');
            }
         }
    });
  });
};

